        url=url+'?id='+id
    }
    axios.get(url)
    .then(res=>{
        console.log(res.data.data)

this.setState({
    data:res.data.data
})
    })
}


render(){
    console.log(this.props.match.params.id)
    return (
        <List
     
      bordered
      dataSource={this.state.data}
      renderItem={item => (
    
          <List.Item>
          <Link to={`/detail/${item}`}>
          {item.title}
          </Link>
          </List.Item>
       
      )}
    />
    )
}
}
export default ListItem;
