import React, { Component, Fragment } from 'react';
import { Modal, Button, Input } from 'antd';
import axios from 'axios';
class Login extends Component{

constructor(props){
    super(props)
    this.state={
        login:false,
        visible:false,
        user:'',
        password:''
    }
    this.handleOk=this.handleOk.bind(this) 
    this.handleCancel=this.handleCancel.bind(this)
    this.handleClick=this.handleClick.bind(this)
    // this.textChange=this.textChange.bind(this)
    // this.passwordChange=this.passwordChange.bind(this)
}

componentDidMount(){
    axios.get('http://www.dell-lee.com/react/api/isLogin.json')
    .then(res=>{
        console.log(res.data.data.login)
        const login=res.data.data.login;
        this.setState({
         login
        })
    })
}



handleClick(){
  this.setState({visible:true}) 
}





handleOk(){
    const { user, password }=this.state
  axios.get(`http://www.dell-lee.com/react/api/login.json?user=${user}&password=${password}`)
  .then(res=>{
      console.log(res)
  })
}

handleCancel(){
    this.setState({visible:false})
}

render(){
    const login=this.state.login;
return(
<Fragment>
    {
        login?
<Button type="primary">退出</Button>:
<Button type="primary" onClick={this.handleClick} >登录</Button>
    }
<Modal
          title="登录"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
        <Input value={this.state.user} style={{ marginBottom:10 }} placeholder="请输入用户名" disabled={false} />
        <Input placeholder="请输入密码" type="password" />
        </Modal>
</Fragment>
        )
    }
}

export default Login;
